import React from 'react';
import data from './levels.json'
import Tracks from './Tracks'
import ChronoTime from './ChronoTime';



function App() {

  /*
  state = {            
    min: 1,
    sec: 0.0,
    mili: 0.0
  }
  */

  state = {
    lap: TimeSpan(0,0,1,0,0)
  }

  setTime = (unit, selection) => 
  {        
      switch(unit) {
          case 'm': 
              this.state.min = selection;
              break;
          
          case 's':
              this.state.sec = selection;
              break;

          case 'ms':
              this.state.mili = selection;

      }
      
  };


  return (
    <div>
      <h1>F1 2022 Difficulty Helper</h1>
      <div>
          <ChronoTime valueonChange={setTime(this.state)} />
          <Tracks data={data} />
      </div>
    </div>
  );
}

export default App;
