import React, { Component } from 'react'
import InputNumber from 'react-input-number';

class ChronoTime extends React.Component
{

    state = {        
        min: 1,
        sec: 0.0,
        mili: 0.0
    }

    setTime = (unit, selection) => 
    {        
        switch(unit) {
            case 'm': 
                this.state.min = selection;
                break;

            case 's':
                this.state.sec = selection;
                break;

            case 'ms':
                this.state.mili = selection;

        }

    };

     render() {
        return (
            <div>
                <InputNumber
                    id='timeMin'
                    min={0}
                    max={9}
                    step={1}
                    value={this.state.min}
                    onChange={(selectedMin) => this.setTime('m', selectedMin) }
                />
                <InputNumber
                    id='timeSec'
                    min={0}
                    max={59}
                    step={1}
                    value={this.state.sec}
                    onChange={(selectedSec) => this.setTime('s', selectedSec) }
                />
                <InputNumber
                    id='timeMili'
                    min={0}
                    max={999}
                    step={1}
                    value={this.state.mili}
                    onChange={(selectedMili) => this.setTime('ms',selectedMili) }
                />
            </div>
        );
    }

    value() {
        return this.state;
    }


}

export default ChronoTime;
