import React, { Component } from 'react'
import InputNumber from 'react-input-number';

import luxon, { DateTime } from 'luxon'

class ChronoTime extends React.Component
{

     render() {
        return (
            <div>
                <InputNumber
                    id='timeMin'
                    min={0}
                    max={9}
                    step={1}
                    value={DateTime.fromMillis(this.props.value).minute}
                    onChange={(selectedMin) => this.props.onChange(
                        DateTime.fromFormat(
                            concat(selectedMin, ':', timeSec.value, timeMili.value), "m:ss:SSS"
                        )                        
                    ) }
                />
                <InputNumber
                    id='timeSec'
                    min={0}
                    max={59}
                    step={1}
                    value={DateTime.fromMillis(this.props.value).second}
                    onChange={(selectedSec) => this.setTime('s', selectedSec) }
                />
                <InputNumber
                    id='timeMili'
                    min={0}
                    max={999}
                    step={1}
                    value={DateTime.fromMillis(this.props.value).millisecond}
                    onChange={(selectedMili) => this.setTime('ms',selectedMili) }
                />
            </div>
        );
    }



}

export default ChronoTime;
