import React from 'react';
import data from './levels.json'
import Tracks from './Tracks'
import ChronoTime from './ChronoTime';


function App() {

  this.state = {
    lap: 0
  }

  const setTime = (time) => 
  {        
    this.state.lap = time;
  };


  return (
    <div>
      <h1>F1 2022 Difficulty Helper</h1>
      <div>
          <ChronoTime 
            value={this.state.lap} 
            onChange={setTime(t)} />
          <Tracks 
            data={data}
            time={this.state.lap} />
      </div>
    </div>
  );
}

export default App;
