import React, { Component } from 'react'
import InputNumber from 'react-input-number';

import luxon, { DateTime } from 'luxon'

class ChronoTime extends React.Component
{

    constructor(props) {
        super(props);

        var time = DateTime.fromMillis(this.props.value)

        this.state = {
          timeMin: time.minute,
          timeSec: time.second,
          timeMilli: time.millisecond
        };

      }




     render() {       
        const handleChange =  (unit, selection ) => 
        {  
          var newTime;

          console.log('Unit:', unit);
          console.log('Selection:', selection);
          console.log('State:', JSON.stringify(this.state));

          switch(unit)
          {
              case 'm':                
                  newTime = DateTime.fromFormat(''.concat(selection, ':', this.state.timeSec, this.state.timeMilli), "m:ss:SSS")
                  break;

              case 's':
                  newTime = DateTime.fromFormat(''.concat(this.state.timeMin, ':', selection, this.state.timeMilli), "m:ss:SSS")
                  break;

              case 'ms':
                  newTime = DateTime.fromFormat(''.concat(this.state.timeMin, ':', this.state.timeSec, selection), "m:ss:SSS")
                  break;

              default:
                  break;

          }

          console.log(newTime);

          this.props.onChange(newTime);

        }                        



        return (
            <div>
                <InputNumber
                    id='timeMin'
                    min={0}
                    max={9}
                    step={1}
                    value={DateTime.fromMillis(this.props.value).minute}
                    onChange={ (selection) => handleChange('m',selection) }
                />
                <InputNumber
                    id='timeSec'
                    min={0}
                    max={59}
                    step={1}
                    value={DateTime.fromMillis(this.props.value).second}
                    onChange={(selection) => handleChange('s', selection) }
                />
                <InputNumber
                    id='timeMili'
                    min={0}
                    max={999}
                    step={1}
                    value={DateTime.fromMillis(this.props.value).millisecond}
                    onChange={(selection) => handleChange('ms',selection) }
                />
            </div>
        );
    }



}

export default ChronoTime;
