import React from 'react';
import { useState } from 'react';

import data from './levels.json'
import Tracks from './Tracks'
import ChronoTime from './ChronoTime';


function App() {

  const [lap, setLap] = useState(85123);  

  const handleChange = (t) => {
    
    console.log("New state lap: ", t);
    setLap(t);

  };

  return (
    <div class='container-fluid'>
      <div class='row'>
        <div class='col-sm'>
          <img src='assets/f122-nav-logo.svg'/>
        </div>
        <div class='col'></div>
      </div>
      <div class='row'>
        <div class='col'>
          <h1>F1 2022 Difficulty Helper</h1>
        </div>
      </div>
      <div id='row lap-input'>
        <div class='col'>
          <h2>Your average lap time</h2>
          <ChronoTime 
            value={lap} 
            onChange={(t) => handleChange(t)} />
        </div>
      </div>
      <div id='row track-input'>
        <div class='col'>
          <h2>Track</h2>
          <Tracks 
            data={data}
            targetLap={lap} />
        </div>
      </div>
    </div>
  );
}

export default App;
